import React from 'react';

const Card = ({name, email, id}) => {
	return (

		<div class="bg-light-blue dib br3 pa3 ma2 grow bw2 shadow-5 tc">
		<img alt='robos' src={`https://robohash.org/${id}?size=200x200`}/>
		<div>
			
		<h2>{name}</h2>

		<p>{email}</p>
		</div>
		

		</div>
		);
}

export default Card;
